function SRAND(seed) {
    let a = Number(seed);
    let t = a += 0x6D2B79F5;
    t = Math.imul(t ^ t >>> 15, t | 1);
    t ^= t + Math.imul(t ^ t >>> 7, t | 61);
    t = (t ^ t >>> 14) >>> 0;
    return t / 4294967296;
  }

var Seed = 0

function maskLongWords(stringArray, maskChar = '-') {
    return stringArray.map(sentence => {
        const words = sentence.split(' ');
        
      const maskedWords = words.map(word => {
        if (word.length <= 2) {
          return word;
        }
        
        const firstTwoLetters = word.substring(0, 2);
        const mask = maskChar.repeat(word.length - 2);
        
        return firstTwoLetters + mask;
      });
  
      return maskedWords.join(' ');
    });
  }
  

function getRandomItemID() {
    var r = SRAND(seed)
    const randomIndex = Math.floor(r * IDS.length);
    return IDS[randomIndex];
}

function compareItems(correctItem, guessedItem) {
    // Compares guessedItem's value (val1) to correctItem's value (val2)
    const compareValues = (val1, val2) => {
        if (val2 > val1) return ">"; // Correct answer is higher
        if (val2 < val1) return "<"; // Correct answer is lower
        return "="; // They are equal
    };
    
    const compareObtainedArrays = (arr1, arr2) => {
        const set1 = new Set(arr1);
        const set2 = new Set(arr2);

        // Helper function to check if sets are equal
        const areSetsEqual = (s1, s2) => {
            if (s1.size !== s2.size) {
                return false;
            }
            // Check if every item in set1 is also in set2
            for (const item of s1) {
                if (!s2.has(item)) {
                    return false;
                }
            }
            return true;
        };

        // 1. Check if the sets are identical
        if (areSetsEqual(set1, set2)) {
            return 2;
        }

        // 2. Check if there is any intersection
        // We loop through one set and see if any of its elements exist in the other
        let hasIntersection = false;
        for (const item of set1) {
            if (set2.has(item)) {
                hasIntersection = true;
                break; // Exit early once we find one common element
            }
        }

        if (hasIntersection) {
            return 1; // They have common elements but are not identical
        } else {
            return 0; // No common elements found
        }
    }

    const getIndex = (array,item) => {
        var ret = array.indexOf(item)
        console.log(ret)
        return ret
    }


    const comparison = {
        "autoswing": correctItem.autoswing === guessedItem.autoswing,
        "damage": compareValues(parseInt(guessedItem.damage), parseInt(correctItem.damage)),
        "damageType": correctItem.damageType === guessedItem.damageType,
        "knockback": compareValues (getIndex(KNOCKBACK,guessedItem.knockback), getIndex(KNOCKBACK,correctItem.knockback)),
        "speed": compareValues     (getIndex(SPEED,guessedItem.speed), getIndex(SPEED,correctItem.speed)),
        "rarity": compareValues(parseInt(guessedItem.rarity), parseInt(correctItem.rarity)),
        "obtained": compareObtainedArrays(guessedItem.obtained, correctItem.obtained),
        "material": correctItem.material === guessedItem.material
    };

    return comparison;
}

