function renderNextItem(item, comped) {
    const tableBody = document.getElementById('resultsBody');
    const newRow = document.createElement('tr');
    const makeReadable = (ele) => {
        return ele ? "Yes" : "No"
    }

    // Add class="stat-cell" to every <td> element
    newRow.innerHTML = `
        <td class="stat-cell"><img src="static/images/${item.id}.png" alt="${item.name || 'Item Image'}"></td>
        <td class="stat-cell" style="background-color: ${getColor("T", comped)}">${item.damageType}</td>
        <td class="stat-cell" style="background-color: ${getColor("D", comped)}">${comped.damage} ${item.damage}</td>
        <td class="stat-cell" style="background-color: ${getColor("K", comped)}">${comped.knockback} ${item.knockback}</td>
        <td class="stat-cell" style="background-color: ${getColor("S", comped)}">${comped.speed} ${item.speed}</td>
        <td class="stat-cell" style="color: ${RARITYCOLOR[parseInt(item.rarity)]};">${comped.rarity} ${RARITY[parseInt(item.rarity)]}</td>
        <td class="stat-cell" style="background-color: ${getColor("A", comped)}">${makeReadable(item.autoswing)}</td>
        <td class="stat-cell" style="background-color: ${getColor("M", comped)}">${makeReadable(item.material)}</td>
        <td class="stat-cell" style="background-color: ${getColor("O", comped)}">${item.obtained}</td>
    `;

    tableBody.prepend(newRow);
}

function getColor(type,comped)
{
    switch (type) {
        case "T":
            if (comped.damageType == true)
            {
                return BACKGROUNDCOLORS[3]
            }else{
                return BACKGROUNDCOLORS[1]
            }
            break;
        case "D":
            if (comped.damage == "=")
            {
                return BACKGROUNDCOLORS[3]
            }else{
                return BACKGROUNDCOLORS[1]
            }
            break;
        case "K":
            if (comped.knockback == "=")
            {
                return BACKGROUNDCOLORS[3]
            }else{
                return BACKGROUNDCOLORS[1]
            }
            break;
        case "S":
            if (comped.speed == "=")
            {
                return BACKGROUNDCOLORS[3]
            }else{
                return BACKGROUNDCOLORS[1]
            }
            break;
        case "A":
            if (comped.autoswing == true)
            {
                return BACKGROUNDCOLORS[3]
            }else{
                return BACKGROUNDCOLORS[1]
            }
            break;
        case "M":
            if (comped.material == true)
            {
                return BACKGROUNDCOLORS[3]
            }else{
                return BACKGROUNDCOLORS[1]
            }
            break;
        case "O":
            if (comped.obtained == 2)
            {
                return BACKGROUNDCOLORS[3]
            }else if (comped.obtained == 1){
                return BACKGROUNDCOLORS[2]
            }else{
                return BACKGROUNDCOLORS[1]
            }
            break;
    }
}

// --- SEARCH/FILTER SCRIPT ---
const searchInput = document.getElementById("guessInput");
const searchMenu = document.getElementById("menu");
searchSetup();

// 1. Populate the dropdown list with all weapon names
function searchSetup()
{
    const weapons = ITEMSDATA.weapons;
    weapons.forEach(item => {
        let li = document.createElement('li');
        li.textContent = item.name;
        let img = document.createElement('img')
        img.src = `static/images/${item.id}.png`;
        li.addEventListener('click', () => {
            searchInput.value = li.textContent; // Fill input on click
            searchMenu.style.display = 'none'; // Hide menu
        });
        li.appendChild(img)
        searchMenu.appendChild(li);
    });
}

// 2. Filter the list on every keypress
const searchFunction = () => {
    const filter = searchInput.value.toUpperCase();
    const li_items = searchMenu.getElementsByTagName("li");
    let hasVisibleItems = false;

    if (filter.length === 0) {
        searchMenu.style.display = 'none';
        return;
    }

    for (let i = 0; i < li_items.length; i++) {
        if (li_items[i].innerHTML.toUpperCase().indexOf(filter) > -1) {
            li_items[i].style.display = "";
            hasVisibleItems = true;
        } else {
            li_items[i].style.display = "none";
        }
    }
    
    searchMenu.style.display = hasVisibleItems ? 'block' : 'none';
};

// 3. Attach the search function to the input box
searchInput.addEventListener('keyup', searchFunction);

function canShow(tn)
{
    if (won) return true
    switch (tn) {
        case "SP":
            return (guessed.length > 2)
            break;
        case "TT":
            return (guessed.length > 5)               
            break;
        case "IM":
            return (guessed.length > 8)
            break;
        default:
            break;
    }
}

function openTIP(evt, tabName) {
    var i, tabcontent, tablinks;
    tabcontent = document.getElementsByClassName("tabcontent");
    for (i = 0; i < tabcontent.length; i++) {
      tabcontent[i].style.display = "none";
    }

    tablinks = document.getElementsByClassName("tablinks");
    for (i = 0; i < tablinks.length; i++) {
      tablinks[i].className = tablinks[i].className.replace(" active", "");
    }

    // alert(canShow(tabName))
    if (canShow(tabName)) {
      document.getElementById(tabName).style.display = "block";
      evt.currentTarget.className += " active";
    } else {
        switch (tabName) {
            case "SP":
                alert("Must Have At Least 3 Guesses")
                break;
            case "TT":
                alert("Must Have At Least 6 Guesses")
                break;
            case "IM":
                alert("Must Have At Least 9 Guesses")
                break;
            default:
                break;
        }
    }
  }
  
  